	]
	return (
		<nav className="nav">
			<div>
				<div onClick={() => {
					navMenuHandler();
					toggleMenuClickHandler();
				}}
				className="burger"
				style={{
					background: isMenuClicked ? 'transparent' : null,
				}}
				>
					<div className="burger__patty"></div>
				</div>

