import { useRef, useState } from "react"
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa'
import projectItem1 from "../img/project-item-01.jpg"
import projectItem2 from "../img/project-item-02.jpg"
import projectItem3 from "../img/project-item-03.jpg"

function Projects () {
	const containerRef = useRef(null)
	const [hoverColor, setHoverColor] = useState('rgb(141, 137, 137)')
	const projectArray = [
		{
			thumbnail: projectItem1,
			alt: "project",
			title: "Best Design",
			description: "Lorem ipsum dolor, adipis scing elit etiam ante vehicula, aliquam mauris in, luctus neque.",
			link: "#",
			buttonText: "More",
		},
		{
			thumbnail: projectItem2,
			alt: "project",
			title: "Creative Pen",
			description: "Lorem ipsum dolor, adipis scing elit etiam ante vehicula, aliquam mauris in, luctus neque.",
			link: "#",
			buttonText: "More",
		},
		{
			thumbnail: projectItem3,
			alt: "project",
			title: "Nice Capture",
			description: "Lorem ipsum dolor, adipis scing elit etiam ante vehicula, aliquam mauris in, luctus neque.",
			link: "#",
			buttonText: "More",
		},
		{
			thumbnail: projectItem1,
			alt: "project",
			title: "Best Design",
			description: "Lorem ipsum dolor, adipis scing elit etiam ante vehicula, aliquam mauris in, luctus neque.",
			link: "#",
			buttonText: "More",
		},
		{
			thumbnail: projectItem2,
			alt: "project",
			title: "Creative Pen",
			description: "Lorem ipsum dolor, adipis scing elit etiam ante vehicula, aliquam mauris in, luctus neque.",
			link: "#",
			buttonText: "More",
		},
		{
			thumbnail: projectItem3,
			alt: "project",
			title: "Nice Capture",
			description: "Lorem ipsum dolor, adipis scing elit etiam ante vehicula, aliquam mauris in, luctus neque.",
			link: "#",
			buttonText: "More",
		},
		{
			thumbnail: projectItem1,
			alt: "project",
			title: "Best Design",
			description: "Lorem ipsum dolor, adipis scing elit etiam ante vehicula, aliquam mauris in, luctus neque.",
			link: "#",
			buttonText: "More",
		},
		{
			thumbnail: projectItem2,
			alt: "project",
			title: "Creative Pen",
			description: "Lorem ipsum dolor, adipis scing elit etiam ante vehicula, aliquam mauris in, luctus neque.",
			link: "#",
			buttonText: "More",
		},
		{
			thumbnail: projectItem3,
			alt: "project",
			title: "Nice Capture",
			description: "Lorem ipsum dolor, adipis scing elit etiam ante vehicula, aliquam mauris in, luctus neque.",
			link: "#",
			buttonText: "More",
		},
		// {
		// 	thumbnail: projectItem3,
		// 	alt: "project",
		// 	title: "Nice Capture",
		// 	description: "Lorem ipsum dolor, adipis scing elit etiam ante vehicula, aliquam mauris in, luctus neque.",
		// 	link: "#",
		// 	buttonText: "More",
		// },
	]
	const chunkedProjects = [];
	for (let i = 0; i < projectArray.length; i += 6) {
		chunkedProjects.push(projectArray.slice(i, i + 6));
	}
	const scrollLeft = () => {
		if (containerRef.current) {
			containerRef.current.scrollBy({ left: -containerRef.current.offsetWidth, behavior: 'smooth' });
		}
	};
	const scrollRight = () => {
		if (containerRef.current) {
			containerRef.current.scrollBy({ left: containerRef.current.offsetWidth, behavior: 'smooth' });
		}
	};
	return (
		<>
			{/* projects section */}
			<section className="panel b-red" id="3">
				<article className="panel__wrapper">
					<div className="panel__content">
						<div className="container">
							<div className="row">
								<div className="col-md-8 col-md-offset-2">
									<div className="projects-content">
										<div className="heading">
											<h4>Recent Projects</h4>
										</div>
										<div className="row">
											<div className="col-md-12">
												<div
													style={{display: 'flex', flexDirection: 'column'}}
												>
													<>
													<div
													style={{ position: 'relative' }}
													>
														{/* Left Arrow */}
														{/* <button
															onClick={scrollLeft}
															style={{
															position: 'absolute',
															left: '-30%',
															// left: 0,
															top: '50%',
															transform: 'translateY(-50%)',
															zIndex: 1100,
															background: '#0303036e',
															// border: '1px solid #ccc',
															borderRadius: 5,
															padding: '0.5rem',
															cursor: 'pointer',
															color: '#fff',
															}}
														>
															←
														</button> */}
														<div
														onClick={scrollLeft}
														onMouseEnter={() => setHoverColor('#1f2327')}
														// onMouseLeave={() => setHoverColor('rgb(214, 207, 207)')}
														style={{
															position: 'absolute',
															left: '-36%',
															// left: 0,
															top: '50%',
															transform: 'translateY(-50%)',
															// zIndex: 1100,
															background: '#0303036e',
															// background: '#fff',
															// border: '1px solid #ccc',
															borderRadius: 5,
															padding: '20px 10px',
															cursor: 'pointer',
															// color: '#fff',
															}}
														>
															<FaChevronLeft size={55} color={hoverColor} />
														</div>
														<div ref={containerRef} className="owl-carousel owl-theme projectContainer">
														{/* {projectArray.map((project, index) => (
															<div key={index}>
																<div className="project-item">
																	<a href={project.link} data-lightbox="image-1">
																		<img src={project.thumbnail} alt={`${project.alt}-${index+1}`}/>
																	</a>
																	<div className="text-content">
																		<h4>{project.title} {index+1}</h4>
																		<p className="project_paragraph">{project.description}</p>
																		<div className="primary-button">
																			<a href={project.link}>{project.buttonText}</a>
																		</div>
																	</div>
																</div>
															</div>)
														)} */}
														{chunkedProjects.map((group, i) => (
															<div key={i} className="projectPage">
															{group.map((project, index) => (
																<div key={index} className="project-item">
																<a href={project.link} data-lightbox="image-1">
																	<img src={project.thumbnail} alt={`${project.alt}-${index + 1}`} />
																</a>
																<div className="text-content">
																	<h4>{project.title} {index + 1}</h4>
																	<p className="project_paragraph">{project.description}</p>
																	<div className="primary-button">
																	<a href={project.link}>{project.buttonText}</a>
																	</div>
																</div>
																</div>
															))}
															</div>
														))}
														</div>
														{/* Right Arrow */}
														<button
															onClick={scrollRight}
															style={{
															position: 'absolute',
															right: 0,
															top: '50%',
															transform: 'translateY(-50%)',
															zIndex: 1,
															background: '#fff',
															border: '1px solid #ccc',
															borderRadius: '50%',
															padding: '0.5rem',
															cursor: 'pointer'
															}}
														>
															→
														</button>
														</div>
													</>
												</div>
											</div>
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>
				</article>
			</section>
		</>
	)
}
export {Projects}
